import React, { useState } from 'react';

const ClienteSelector = ({ cliente, setCliente, onClienteSeleccionado }) => {
    const [mostrarFormulario, setMostrarFormulario] = useState(false);

    const capturarGeolocalizacion = () => {
        alert("¬°Geolocalizaci√≥n funcionando!");
    };

    return (
        <div style={{ padding: '1rem' }}>
            <h3>üë§ Seleccionar Cliente</h3>
            
            <button onClick={() => setMostrarFormulario(true)}>
                ‚ûï Crear Cliente
            </button>

            {mostrarFormulario && (
                <div style={{ marginTop: '1rem', padding: '1rem', border: '1px solid #007bff' }}>
                    <h4>Formulario de prueba</h4>
                    <button onClick={capturarGeolocalizacion}>
                        üìç Capturar Ubicaci√≥n
                    </button>
                    <button onClick={() => setMostrarFormulario(false)} style={{ marginLeft: '1rem' }}>
                        ‚ùå Cancelar
                    </button>
                </div>
            )}
        </div>
    );
};

export default ClienteSelector;
