import React, { useState, useEffect } from 'react';
import API from '../services/api';

const ClienteSelector = ({ cliente, setCliente }) => {
    const [clientes, setClientes] = useState([]);
    const [nuevoCliente, setNuevoCliente] = useState({
        numeroCliente: '',
        razonSocial: '',
        direccion: '',
        telefono: '',
        idLocalidad: '',
        cuit: '',
        saldo: 0
    });
    const [mostrarFormulario, setMostrarFormulario] = useState(false);
    const [localidades, setLocalidades] = useState([]);

    // Cargar clientes y localidades al cargar el componente
    useEffect(() => {
        const cargarDatos = async () => {
            try {
                const [clientesRes, localidadesRes] = await Promise.all([
                    API.get('/clientes'),
                    API.get('/catalogos/localidades')
                ]);
                setClientes(clientesRes.data);
                setLocalidades(localidadesRes.data);
            } catch (error) {
                console.error("Error al cargar datos:", error);
            }
        };
        cargarDatos();
    }, []);

    // Generar número de cliente automático
    const generarNumeroCliente = () => {
        if (clientes.length === 0) {
            return 'CL001';
        }

        const ultimoCliente = clientes.reduce((prev, curr) => {
            const numPrev = parseInt(prev.numeroCliente.replace('CL', ''), 10);
            const numCurr = parseInt(curr.numeroCliente.replace('CL', ''), 10);
            return numCurr > numPrev ? curr : prev;
        });

        const num = parseInt(ultimoCliente.numeroCliente.replace('CL', ''), 10) + 1;
        return `CL${num.toString().padStart(3, '0')}`;
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNuevoCliente(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleCrearCliente = async () => {
        if (!nuevoCliente.razonSocial || !nuevoCliente.direccion || !nuevoCliente.idLocalidad || !nuevoCliente.cuit) {
            alert("Por favor complete todos los campos obligatorios.");
            return;
        }

        try {
            const clienteCreado = await API.post('/clientes', {
                ...nuevoCliente,
                numeroCliente: generarNumeroCliente(),
                saldo: 0
            });

            setClientes(prev => [...prev, clienteCreado.data]);
            setCliente(clienteCreado.data);
            setMostrarFormulario(false);
            setNuevoCliente({
                numeroCliente: '',
                razonSocial: '',
                direccion: '',
                telefono: '',
                idLocalidad: '',
                cuit: '',
                saldo: 0
            });
        } catch (error) {
            console.error("Error al crear cliente:", error);
            alert("Error al crear cliente. Verifique los datos.");
        }
    };

    const handleSeleccionarCliente = (cliente) => {
        setCliente(cliente);
    };

    const handleCancelarAlta = () => {
        setMostrarFormulario(false);
        setNuevoCliente({
            numeroCliente: '',
            razonSocial: '',
            direccion: '',
            telefono: '',
            idLocalidad: '',
            cuit: '',
            saldo: 0
        });
    };

    return (
        <div style={{ padding: '1rem' }}>
            <h3>👤 Seleccionar Cliente</h3>

            {/* Buscar cliente */}
            <div style={{ marginBottom: '1rem' }}>
                <input
                    type="text"
                    placeholder="Buscar por número o razón social"
                    style={{ width: '100%', padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}
                />
            </div>

            {/* Lista de clientes */}
            <div style={{ maxHeight: '200px', overflowY: 'auto', border: '1px solid #ddd', padding: '0.5rem', marginBottom: '1rem' }}>
                {clientes.map(c => (
                    <div
                        key={c.id}
                        style={{
                            padding: '0.5rem',
                            borderBottom: '1px solid #eee',
                            cursor: 'pointer',
                            backgroundColor: cliente?.id === c.id ? '#e9f7ef' : 'white'
                        }}
                        onClick={() => handleSeleccionarCliente(c)}
                    >
                        <strong>{c.numeroCliente}</strong> - {c.razonSocial} - {c.localidad.nombre}
                    </div>
                ))}
            </div>

            {/* Botón para crear cliente */}
            <button
                onClick={() => setMostrarFormulario(true)}
                style={{
                    padding: '0.5rem 1rem',
                    backgroundColor: '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                }}
            >
                ➕ Crear Cliente
            </button>

            {/* Formulario de alta de cliente */}
            {mostrarFormulario && (
                <div style={{ marginTop: '1rem', border: '1px dashed #007bff', padding: '1rem', borderRadius: '8px' }}>
                    <h4>Nuevo Cliente</h4>
                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
                        <div>
                            <label>Número de Cliente:</label>
                            <input
                                type="text"
                                value={generarNumeroCliente()}
                                readOnly
                                style={{ width: '100%', padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}
                            />
                        </div>
                        <div>
                            <label>Razón Social:</label>
                            <input
                                type="text"
                                name="razonSocial"
                                value={nuevoCliente.razonSocial}
                                onChange={handleInputChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}
                            />
                        </div>
                        <div>
                            <label>Dirección:</label>
                            <input
                                type="text"
                                name="direccion"
                                value={nuevoCliente.direccion}
                                onChange={handleInputChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}
                            />
                        </div>
                        <div>
                            <label>Teléfono:</label>
                            <input
                                type="text"
                                name="telefono"
                                value={nuevoCliente.telefono}
                                onChange={handleInputChange}
                                style={{ width: '100%', padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}
                            />
                        </div>
                        <div>
                            <label>Localidad:</label>
                            <select
                                name="idLocalidad"
                                value={nuevoCliente.idLocalidad}
                                onChange={handleInputChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}
                            >
                                <option value="">Seleccionar localidad</option>
                                {localidades.map(l => (
                                    <option key={l.id} value={l.id}>{l.nombre}</option>
                                ))}
                            </select>
                        </div>
                        <div>
                            <label>CUIT:</label>
                            <input
                                type="text"
                                name="cuit"
                                value={nuevoCliente.cuit}
                                onChange={handleInputChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', border: '1px solid #ddd', borderRadius: '4px' }}
                            />
                        </div>
                    </div>
                    <div style={{ marginTop: '1rem', display: 'flex', gap: '1rem' }}>
                        <button
                            onClick={handleCrearCliente}
                            style={{
                                padding: '0.5rem 1rem',
                                backgroundColor: '#28a745',
                                color: 'white',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                        >
                            ✅ Crear Cliente
                        </button>
                        <button
                            onClick={handleCancelarAlta}
                            style={{
                                padding: '0.5rem 1rem',
                                backgroundColor: '#6c757d',
                                color: 'white',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                        >
                            ❌ Cancelar
                        </button>
                    </div>
                </div>
            )}

            {/* Botón Siguiente */}
            <div style={{ marginTop: '1rem', textAlign: 'right' }}>
                <button
                    onClick={() => setCliente(cliente)}
                    disabled={!cliente}
                    style={{
                        padding: '0.5rem 1rem',
                        backgroundColor: cliente ? '#28a745' : '#6c757d',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: cliente ? 'pointer' : 'not-allowed'
                    }}
                >
                    → Siguiente
                </button>
            </div>
        </div>
    );
};

export default ClienteSelector;
