import React, { useState, useEffect } from 'react';
import API from '../services/api';

const ClienteSelector = ({ cliente, setCliente }) => {
    const [clientes, setClientes] = useState([]);
    const [busqueda, setBusqueda] = useState('');
    const [mostrarForm, setMostrarForm] = useState(false);
    const [nuevoCliente, setNuevoCliente] = useState({
        NumeroCliente: '',
        RazonSocial: '',
        Direccion: '',
        Telefono: '',
        IdLocalidad: '',
        CUIT: '',
        Saldo: 0
    });

    useEffect(() => {
        const cargarClientes = async () => {
            try {
                const res = await API.get('/clientes');
                setClientes(res.data);
            } catch (error) {
                console.error("Error al cargar clientes", error);
            }
        };
        cargarClientes();
    }, []);

    const handleSelect = (cli) => {
        setCliente(cli);
        setBusqueda('');
    };

    const handleNuevoCliente = async (e) => {
        e.preventDefault();
        try {
            const res = await API.post('/clientes', nuevoCliente);
            alert("Cliente creado con éxito");
            setMostrarForm(false);
            setNuevoCliente({
                NumeroCliente: '',
                RazonSocial: '',
                Direccion: '',
                Telefono: '',
                IdLocalidad: '',
                CUIT: '',
                Saldo: 0
            });
            // Recargar lista
            const resCli = await API.get('/clientes');
            setClientes(resCli.data);
            setCliente({ ...nuevoCliente, IdCliente: res.data.id });
        } catch (error) {
            alert("Error al crear cliente");
        }
    };

    const filtrados = clientes.filter(c =>
        c.NumeroCliente.toLowerCase().includes(busqueda.toLowerCase()) ||
        c.RazonSocial.toLowerCase().includes(busqueda.toLowerCase())
    );

    return (
        <div style={{ margin: '1rem 0', padding: '1rem', border: '1px solid #ddd', borderRadius: '8px' }}>
            <h3>👥 Seleccionar Cliente</h3>
            {cliente && (
                <div style={{ backgroundColor: '#e8f5e9', padding: '1rem', marginBottom: '1rem', borderRadius: '4px' }}>
                    <strong>Cliente seleccionado:</strong> {cliente.RazonSocial} - {cliente.NumeroCliente}<br/>
                    Dirección: {cliente.Direccion} - {cliente.Localidad}, {cliente.Provincia}<br/>
                    Saldo: ${cliente.Saldo}
                    <button onClick={() => setCliente(null)} style={{ marginLeft: '1rem', padding: '0.2rem 0.5rem' }}>
                        Cambiar
                    </button>
                </div>
            )}

            {!cliente && (
                <>
                    <input
                        type="text"
                        placeholder="Buscar por número o razón social"
                        value={busqueda}
                        onChange={(e) => setBusqueda(e.target.value)}
                        style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
                    />

                    <div style={{ maxHeight: '200px', overflowY: 'auto', border: '1px solid #ccc', padding: '0.5rem', marginBottom: '1rem' }}>
                        {filtrados.map(c => (
                            <div
                                key={c.IdCliente}
                                onClick={() => handleSelect(c)}
                                style={{
                                    padding: '0.5rem',
                                    cursor: 'pointer',
                                    borderBottom: '1px solid #eee'
                                }}
                            >
                                <strong>{c.NumeroCliente}</strong> - {c.RazonSocial} - {c.Localidad}
                            </div>
                        ))}
                    </div>

                    <button
                        onClick={() => setMostrarForm(!mostrarForm)}
                        style={{ padding: '0.5rem', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px' }}
                    >
                        {mostrarForm ? 'Cancelar alta' : '➕ Agregar nuevo cliente'}
                    </button>

                    {mostrarForm && (
                        <form onSubmit={handleNuevoCliente} style={{ marginTop: '1rem', padding: '1rem', border: '1px dashed #007bff', borderRadius: '4px' }}>
                            <input
                                type="text"
                                placeholder="Número de Cliente"
                                value={nuevoCliente.NumeroCliente}
                                onChange={(e) => setNuevoCliente({...nuevoCliente, NumeroCliente: e.target.value})}
                                required
                                style={{ display: 'block', width: '100%', marginBottom: '0.5rem', padding: '0.3rem' }}
                            />
                            <input
                                type="text"
                                placeholder="Razón Social"
                                value={nuevoCliente.RazonSocial}
                                onChange={(e) => setNuevoCliente({...nuevoCliente, RazonSocial: e.target.value})}
                                required
                                style={{ display: 'block', width: '100%', marginBottom: '0.5rem', padding: '0.3rem' }}
                            />
                            <input
                                type="text"
                                placeholder="Dirección"
                                value={nuevoCliente.Direccion}
                                onChange={(e) => setNuevoCliente({...nuevoCliente, Direccion: e.target.value})}
                                style={{ display: 'block', width: '100%', marginBottom: '0.5rem', padding: '0.3rem' }}
                            />
                            <input
                                type="text"
                                placeholder="Teléfono"
                                value={nuevoCliente.Telefono}
                                onChange={(e) => setNuevoCliente({...nuevoCliente, Telefono: e.target.value})}
                                style={{ display: 'block', width: '100%', marginBottom: '0.5rem', padding: '0.3rem' }}
                            />
                            <input
                                type="number"
                                placeholder="ID Localidad (ej: 1)"
                                value={nuevoCliente.IdLocalidad}
                                onChange={(e) => setNuevoCliente({...nuevoCliente, IdLocalidad: e.target.value})}
                                required
                                style={{ display: 'block', width: '100%', marginBottom: '0.5rem', padding: '0.3rem' }}
                            />
                            <input
                                type="text"
                                placeholder="CUIT"
                                value={nuevoCliente.CUIT}
                                onChange={(e) => setNuevoCliente({...nuevoCliente, CUIT: e.target.value})}
                                style={{ display: 'block', width: '100%', marginBottom: '0.5rem', padding: '0.3rem' }}
                            />
                            <input
                                type="number"
                                placeholder="Saldo inicial"
                                value={nuevoCliente.Saldo}
                                onChange={(e) => setNuevoCliente({...nuevoCliente, Saldo: e.target.value})}
                                style={{ display: 'block', width: '100%', marginBottom: '0.5rem', padding: '0.3rem' }}
                            />
                            <button type="submit" style={{ padding: '0.5rem', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px' }}>
                                Crear Cliente
                            </button>
                        </form>
                    )}
                </>
            )}
        </div>
    );
};

export default ClienteSelector;
