import React, { useState, useEffect } from 'react';
import API from '../services/api';
import Carrito from './Carrito';
import BusquedaPorCodigo from './BusquedaPorCodigo';
import ProductoSelector from './ProductoSelector';
import ConfirmacionPedido from './ConfirmacionPedido';

const Pedido = () => {
    const [cliente, setCliente] = useState(null);
    const [carrito, setCarrito] = useState([]);
    const [modo, setModo] = useState(null); // null, 'codigo', 'catalogo'
    const [mostrarConfirmacion, setMostrarConfirmacion] = useState(false);
    const [formasPago, setFormasPago] = useState([]);
    const [listasPrecios, setListasPrecios] = useState([]);
    const [listaPrecios, setListaPrecios] = useState('');
    const [formaPago, setFormaPago] = useState('');

    useEffect(() => {
        const cargarCatalogos = async () => {
            try {
                const [fpRes, lpRes] = await Promise.all([
                    API.get('/catalogos/formas-pago'),
                    API.get('/catalogos/listas-precios')
                ]);
                setFormasPago(fpRes.data);
                setListasPrecios(lpRes.data);
            } catch (error) {
                console.error("Error al cargar cat√°logos", error);
            }
        };
        cargarCatalogos();
    }, []);

    const agregarAlCarrito = (producto, cantidad, precio) => {
        const nuevoItem = {
            id: Date.now(),
            producto,
            cantidad,
            precioUnitario: precio,
            importe: cantidad * precio
        };
        setCarrito(prev => [...prev, nuevoItem]);
    };

    const eliminarDelCarrito = (id) => {
        setCarrito(carrito.filter(item => item.id !== id));
    };

    const total = carrito.reduce((sum, item) => sum + item.importe, 0);

    if (!modo) {
        return (
            <div style={{ padding: '2rem', fontFamily: 'Arial', textAlign: 'center' }}>
                <h1>üì• Cargar Nuevo Pedido</h1>
                <ClienteSelector cliente={cliente} setCliente={setCliente} />
                <br/>
                <button
                    onClick={() => setModo('codigo')}
                    style={{ padding: '1rem 2rem', fontSize: '1.2rem', margin: '0.5rem', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '8px' }}
                >
                    üî¢ Cargar por C√≥digo
                </button>
                <button
                    onClick={() => setModo('catalogo')}
                    style={{ padding: '1rem 2rem', fontSize: '1.2rem', margin: '0.5rem', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '8px' }}
                >
                    üìö Cargar por Cat√°logo
                </button>
            </div>
        );
    }

    if (mostrarConfirmacion) {
        return (
            <ConfirmacionPedido
                carrito={carrito}
                total={total}
                cliente={cliente}
                onVolver={() => setMostrarConfirmacion(false)}
                onConfirmar={() => {
                    alert("‚úÖ Pedido confirmado con √©xito");
                    setCarrito([]);
                    setModo(null);
                    setMostrarConfirmacion(false);
                }}
            />
        );
    }

    return (
        <div style={{ display: 'flex', padding: '1rem', fontFamily: 'Arial', gap: '2rem' }}>
            {/* Panel izquierdo: Formulario de carga */}
            <div style={{ flex: 2 }}>
                <h2>{modo === 'codigo' ? 'üî¢ Cargar por C√≥digo' : 'üìö Cargar por Cat√°logo'}</h2>

                {modo === 'codigo' && (
                    <BusquedaPorCodigo
                        listaPreciosId={listaPrecios}
                        onAdd={agregarAlCarrito}
                    />
                )}

                {modo === 'catalogo' && listaPrecios && (
                    <ProductoSelector
                        listaPreciosId={listaPrecios}
                        onAdd={agregarAlCarrito}
                    />
                )}

                <div style={{ marginTop: '2rem' }}>
                    <label>üè∑Ô∏è Lista de Precios: </label>
                    <select value={listaPrecios} onChange={(e) => setListaPrecios(e.target.value)} style={{ marginLeft: '1rem', padding: '0.3rem' }}>
                        <option value="">Seleccionar</option>
                        {listasPrecios.map(lp => (
                            <option key={lp.IdLista} value={lp.IdLista}>{lp.Nombre}</option>
                        ))}
                    </select>
                </div>
            </div>

            {/* Panel derecho: Carrito */}
            <div style={{ flex: 1, borderLeft: '2px solid #ddd', paddingLeft: '1rem' }}>
                <Carrito items={carrito} onRemove={eliminarDelCarrito} total={total} />
                <button
                    onClick={() => setMostrarConfirmacion(true)}
                    disabled={carrito.length === 0}
                    style={{
                        padding: '0.7rem 2rem',
                        fontSize: '1.1rem',
                        backgroundColor: '#ffc107',
                        color: 'black',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: carrito.length === 0 ? 'not-allowed' : 'pointer',
                        marginTop: '1rem',
                        width: '100%'
                    }}
                >
                    üìÑ Ver Contenido / Confirmar
                </button>
                <button
                    onClick={() => setModo(null)}
                    style={{
                        padding: '0.5rem',
                        backgroundColor: '#6c757d',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                        marginTop: '0.5rem',
                        width: '100%'
                    }}
                >
                    ‚Üê Cambiar modo
                </button>
            </div>
        </div>
    );
};

// Componente ClienteSelector (incrustado por simplicidad)
const ClienteSelector = ({ cliente, setCliente }) => {
    const [clientes, setClientes] = useState([]);
    const [busqueda, setBusqueda] = useState('');

    useEffect(() => {
        const cargarClientes = async () => {
            try {
                const res = await API.get('/clientes');
                setClientes(res.data);
            } catch (error) {
                console.error("Error al cargar clientes", error);
            }
        };
        cargarClientes();
    }, []);

    const handleSelect = (cli) => {
        setCliente(cli);
        setBusqueda('');
    };

    const filtrados = clientes.filter(c =>
        c.NumeroCliente.toLowerCase().includes(busqueda.toLowerCase()) ||
        c.RazonSocial.toLowerCase().includes(busqueda.toLowerCase())
    );

    if (cliente) {
        return (
            <div style={{ backgroundColor: '#e8f5e9', padding: '1rem', marginBottom: '1rem', borderRadius: '4px', textAlign: 'left' }}>
                <strong>Cliente:</strong> {cliente.RazonSocial} ({cliente.NumeroCliente})<br/>
                <button onClick={() => setCliente(null)} style={{ marginTop: '0.5rem', padding: '0.2rem 0.5rem' }}>
                    Cambiar
                </button>
            </div>
        );
    }

    return (
        <div style={{ marginBottom: '2rem', textAlign: 'left' }}>
            <input
                type="text"
                placeholder="Buscar cliente por n√∫mero o raz√≥n social"
                value={busqueda}
                onChange={(e) => setBusqueda(e.target.value)}
                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
            />
            <div style={{ maxHeight: '150px', overflowY: 'auto', border: '1px solid #ccc', padding: '0.5rem' }}>
                {filtrados.map(c => (
                    <div
                        key={c.IdCliente}
                        onClick={() => handleSelect(c)}
                        style={{
                            padding: '0.5rem',
                            cursor: 'pointer',
                            borderBottom: '1px solid #eee'
                        }}
                    >
                        <strong>{c.NumeroCliente}</strong> - {c.RazonSocial}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Pedido;
