import React, { useState, useEffect } from 'react';
import API from '../services/api';
import ClienteSelector from './ClienteSelector';
import ProductoSelector from './ProductoSelector';
import Carrito from './Carrito';

const Pedido = () => {
    const [cliente, setCliente] = useState(null);
    const [fechaVencimiento, setFechaVencimiento] = useState('');
    const [listaPrecios, setListaPrecios] = useState('');
    const [formaPago, setFormaPago] = useState('');
    const [carrito, setCarrito] = useState([]);
    const [formasPago, setFormasPago] = useState([]);
    const [listasPrecios, setListasPrecios] = useState([]);

    useEffect(() => {
        const cargarCatalogos = async () => {
            try {
                const [fpRes, lpRes] = await Promise.all([
                    API.get('/catalogos/formas-pago'),
                    API.get('/catalogos/listas-precios')
                ]);
                setFormasPago(fpRes.data);
                setListasPrecios(lpRes.data);
            } catch (error) {
                console.error("Error al cargar cat√°logos", error);
            }
        };
        cargarCatalogos();
    }, []);

    const agregarAlCarrito = (producto, cantidad, precio) => {
        const nuevoItem = {
            id: Date.now(),
            producto,
            cantidad,
            precioUnitario: precio,
            importe: cantidad * precio
        };
        setCarrito([...carrito, nuevoItem]);
    };

    const eliminarDelCarrito = (id) => {
        setCarrito(carrito.filter(item => item.id !== id));
    };

    const total = carrito.reduce((sum, item) => sum + item.importe, 0);

    const confirmarPedido = async () => {
        if (!cliente || !fechaVencimiento || !listaPrecios || !formaPago) {
            alert("Complete todos los campos");
            return;
        }

        const pedido = {
            IdCliente: cliente.IdCliente,
            FechaVencimiento: fechaVencimiento,
            IdFormaPago: parseInt(formaPago),
            IdListaPrecios: parseInt(listaPrecios),
            Total: total,
            Detalle: carrito.map(item => ({
                IdProducto: item.producto.IdProducto,
                Cantidad: item.cantidad,
                PrecioUnitario: item.precioUnitario,
                Importe: item.importe
            }))
        };

        try {
            await API.post('/pedidos', pedido);
            alert("‚úÖ Pedido creado con √©xito");
            setCarrito([]);
            setCliente(null);
            setFechaVencimiento('');
            setListaPrecios('');
            setFormaPago('');
        } catch (error) {
            alert("‚ùå Error al crear pedido");
            console.error(error);
        }
    };

    return (
        <div style={{ padding: '2rem', fontFamily: 'Arial' }}>
            <h1>üìù Nuevo Pedido</h1>

            <ClienteSelector cliente={cliente} setCliente={setCliente} />

            <div style={{ margin: '1rem 0' }}>
                <label>üìÖ Fecha de Vencimiento: </label>
                <input
                    type="date"
                    value={fechaVencimiento}
                    onChange={(e) => setFechaVencimiento(e.target.value)}
                    style={{ marginLeft: '1rem', padding: '0.3rem' }}
                />
            </div>

            <div style={{ margin: '1rem 0' }}>
                <label>üè∑Ô∏è Lista de Precios: </label>
                <select value={listaPrecios} onChange={(e) => setListaPrecios(e.target.value)} style={{ marginLeft: '1rem', padding: '0.3rem' }}>
                    <option value="">Seleccionar</option>
                    {listasPrecios.map(lp => (
                        <option key={lp.IdLista} value={lp.IdLista}>{lp.Nombre}</option>
                    ))}
                </select>
            </div>

            <div style={{ margin: '1rem 0' }}>
                <label>üí≥ Forma de Pago: </label>
                <select value={formaPago} onChange={(e) => setFormaPago(e.target.value)} style={{ marginLeft: '1rem', padding: '0.3rem' }}>
                    <option value="">Seleccionar</option>
                    {formasPago.map(fp => (
                        <option key={fp.IdPago} value={fp.IdPago}>{fp.Descripcion}</option>
                    ))}
                </select>
            </div>

            {listaPrecios && <ProductoSelector listaPreciosId={listaPrecios} onAdd={agregarAlCarrito} />}

            <Carrito items={carrito} onRemove={eliminarDelCarrito} total={total} />

            <button
                onClick={confirmarPedido}
                disabled={carrito.length === 0}
                style={{
                    padding: '0.7rem 2rem',
                    fontSize: '1.1rem',
                    backgroundColor: '#28a745',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: carrito.length === 0 ? 'not-allowed' : 'pointer',
                    marginTop: '1rem'
                }}
            >
                ‚úÖ Confirmar Pedido
            </button>
        </div>
    );
};

export default Pedido;
