import React, { useState } from 'react';
import { PDFViewer, PDFDownloadLink } from '@react-pdf/renderer';
import ReportePedidoPDF from './ReportePedidoPDF';
import API from '../services/api'; // ← ¡Importa API!

const ConfirmacionPedido = ({ carrito, total, cliente, fechaVencimiento, formaPago, formasPago, listaPrecios, listasPrecios, onConfirmar }) => {
    const [mostrarPDF, setMostrarPDF] = useState(false);
    const [guardando, setGuardando] = useState(false);
    const [numeroPedido, setNumeroPedido] = useState(null);

    // Obtener nombres descriptivos
const formaPagoNombre = formasPago && Array.isArray(formasPago)
    ? formasPago.find(fp => fp.id == formaPago)?.descripcion || 'No especificada'
    : 'No especificada';

const listaPreciosNombre = listasPrecios && Array.isArray(listasPrecios)
    ? listasPrecios.find(lp => lp.id == listaPrecios)?.nombre || 'No especificada' // ← ¡CORREGIDO!
    : 'No especificada';

    // Datos completos del pedido para el PDF
    const pedidoData = {
        numeroPedido: numeroPedido || 'PENDIENTE',
        cliente,
        fechaVencimiento,
        formaPago: formaPagoNombre,
        listaPrecios: listaPreciosNombre,
        carrito,
        total,
        fecha: new Date().toLocaleDateString()
    };

    const handleConfirmarPedido = async () => {
        setGuardando(true);

        try {
            // ✅ Enviar pedido al backend
            const response = await API.post('/pedidos', {
                idCliente: cliente.id_cliente,
                fechaVencimiento,
                idFormaPago: formaPago,
                idListaPrecios: listaPrecios,
                total,
                carrito
            });

            setNumeroPedido(response.data.numeroPedido);
            setMostrarPDF(true);

        } catch (error) {
            console.error("❌ Error al guardar pedido:", error);
            alert("Error al guardar el pedido. Por favor, inténtelo de nuevo.");
        } finally {
            setGuardando(false);
        }
    };

    return (
        <div style={{ padding: '1rem' }}>
            <h3>📄 Confirmación del Pedido</h3>

            {!mostrarPDF ? (
                <>
                    <div style={{ backgroundColor: '#f8f9fa', padding: '1rem', borderRadius: '8px', marginBottom: '1rem' }}>
                        <h4>Datos del Cliente</h4>
                        <p><strong>{cliente.razon_social}</strong></p>
                        <p>{cliente.direccion} - {cliente.localidad_nombre}</p>
                        <p><strong>CUIT:</strong> {cliente.cuit}</p>
                    </div>

                    <div style={{ backgroundColor: '#fff3cd', padding: '1rem', borderRadius: '8px', marginBottom: '1rem' }}>
                        <h4>Detalles del Pedido</h4>
                        <p><strong>Fecha de Vencimiento:</strong> {fechaVencimiento}</p>
                        <p><strong>Forma de Pago:</strong> {formaPagoNombre}</p>
                        <p><strong>Lista de Precios:</strong> {listaPreciosNombre}</p>
                        <p><strong>Total:</strong> ${total.toFixed(2)}</p>
                    </div>

                    <button
                        onClick={handleConfirmarPedido}
                        disabled={guardando}
                        style={{
                            padding: '0.7rem 2rem',
                            backgroundColor: guardando ? '#6c757d' : '#28a745',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            cursor: guardando ? 'not-allowed' : 'pointer',
                            fontSize: '1.1rem'
                        }}
                    >
                        {guardando ? 'Guardando pedido...' : '✅ Confirmar y Guardar Pedido'}
                    </button>
                </>
            ) : (
                <>
                    <div style={{ backgroundColor: '#d4edda', padding: '1rem', borderRadius: '8px', marginBottom: '1rem' }}>
                        <h4 style={{ color: '#155724' }}>🎉 ¡Pedido guardado exitosamente!</h4>
                        <p><strong>Número de Pedido:</strong> {numeroPedido}</p>
                    </div>

                    <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
                        <PDFDownloadLink
                            document={<ReportePedidoPDF pedido={pedidoData} />}
                            fileName={`pedido_${numeroPedido}.pdf`}
                        >
                            {({ loading }) =>
                                loading ? (
                                    <button style={{ padding: '0.5rem 1rem', backgroundColor: '#ffc107' }}>
                                        Generando PDF...
                                    </button>
                                ) : (
                                    <button
                                        style={{
                                            padding: '0.5rem 1rem',
                                            backgroundColor: '#28a745',
                                            color: 'white',
                                            border: 'none',
                                            borderRadius: '4px',
                                            cursor: 'pointer'
                                        }}
                                    >
                                        💾 Descargar PDF
                                    </button>
                                )
                            }
                        </PDFDownloadLink>

                        <button
                            onClick={onConfirmar}
                            style={{
                                padding: '0.5rem 1rem',
                                backgroundColor: '#007bff',
                                color: 'white',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                        >
                            ← Nuevo Pedido
                        </button>
                    </div>

                    <div style={{ height: '600px', border: '1px solid #ddd', borderRadius: '8px' }}>
                        <PDFViewer width="100%" height="100%">
                            <ReportePedidoPDF pedido={pedidoData} />
                        </PDFViewer>
                    </div>
                </>
            )}
        </div>
    );
};

export default ConfirmacionPedido;
