import React, { useState, useEffect } from 'react';
import API from '../services/api';

const ProductoSelector = ({ listaPreciosId, onAdd }) => {
    const [productos, setProductos] = useState([]);
    const [busqueda, setBusqueda] = useState('');
    const [cantidad, setCantidad] = useState(1);

    useEffect(() => {
        if (!listaPreciosId) return;
        const cargarProductos = async () => {
            try {
                const res = await API.get(`/productos?lista=${listaPreciosId}`);
                setProductos(res.data);
            } catch (error) {
                console.error("Error al cargar productos", error);
            }
        };
        cargarProductos();
    }, [listaPreciosId]);

    const handleAdd = (producto) => {
        if (!cantidad || cantidad <= 0) return alert("Cantidad invÃ¡lida");
        onAdd(producto, parseFloat(cantidad), producto.Precio);
        setCantidad(1);
    };

    const filtrados = productos.filter(p =>
        p.Codigo.toLowerCase().includes(busqueda.toLowerCase()) ||
        p.Descripcion.toLowerCase().includes(busqueda.toLowerCase())
    );

    return (
        <div style={{ margin: '2rem 0', padding: '1rem', border: '1px solid #ddd', borderRadius: '8px' }}>
            <h3>ðŸ“¦ Seleccionar Producto</h3>
            <input
                type="text"
                placeholder="Buscar por cÃ³digo o descripciÃ³n"
                value={busqueda}
                onChange={(e) => setBusqueda(e.target.value)}
                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
            />
            <input
                type="number"
                placeholder="Cantidad"
                value={cantidad}
                onChange={(e) => setCantidad(e.target.value)}
                min="1"
                step="any"
                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem' }}
            />
            <div style={{ maxHeight: '300px', overflowY: 'auto', border: '1px solid #ccc', padding: '1rem' }}>
                {filtrados.map(p => (
                    <div key={p.IdProducto} style={{ padding: '0.7rem', borderBottom: '1px solid #eee', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <div>
                            <strong>{p.Codigo}</strong> - {p.Descripcion}<br/>
                            <small style={{ color: '#d73a49' }}>${p.Precio.toFixed(2)}</small>
                        </div>
                        <button
                            onClick={() => handleAdd(p)}
                            style={{
                                padding: '0.3rem 1rem',
                                backgroundColor: '#007bff',
                                color: 'white',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                        >
                            âž• Agregar
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProductoSelector;
