import React, { useState } from 'react';
import { PDFViewer, PDFDownloadLink } from '@react-pdf/renderer';
import ReportePedidoPDF from './ReportePedidoPDF';

const ConfirmacionPedido = ({ carrito, total, cliente, fechaVencimiento, formaPago, formasPago, onConfirmar }) => {
    const [mostrarPDF, setMostrarPDF] = useState(false);

    // ‚úÖ Obtener nombre de la forma de pago (con validaci√≥n)
    const formaPagoNombre = formasPago && Array.isArray(formasPago)
        ? formasPago.find(fp => fp.id == formaPago)?.descripcion || 'No especificada'
        : 'Cargando...';

    // Datos completos del pedido para el PDF
    const pedido = {
        numeroPedido: 'PED-' + Date.now().toString().slice(-6),
        cliente,
        fechaVencimiento,
        formaPago: formaPagoNombre,
        listaPrecios: "Lista 1", // Puedes pasarlo como prop si lo necesitas
        carrito,
        total,
        fecha: new Date().toLocaleDateString()
    };

    return (
        <div style={{ padding: '1rem' }}>
            <h3>üìÑ Confirmaci√≥n del Pedido</h3>

            {!mostrarPDF ? (
                <>
                    <div style={{ backgroundColor: '#f8f9fa', padding: '1rem', borderRadius: '8px', marginBottom: '1rem' }}>
                        <h4>Datos del Cliente</h4>
                        <p><strong>{cliente.razon_social}</strong></p>
                        <p>{cliente.direccion} - {cliente.localidad_nombre}</p>
                        <p><strong>CUIT:</strong> {cliente.cuit}</p>
                    </div>

                    <div style={{ backgroundColor: '#fff3cd', padding: '1rem', borderRadius: '8px', marginBottom: '1rem' }}>
                        <h4>Detalles del Pedido</h4>
                        <p><strong>Fecha de Vencimiento:</strong> {fechaVencimiento}</p>
                        <p><strong>Forma de Pago:</strong> {formaPagoNombre}</p>
                        <p><strong>Total:</strong> ${total.toFixed(2)}</p>
                    </div>

                    <button
                        onClick={() => setMostrarPDF(true)}
                        style={{
                            padding: '0.7rem 2rem',
                            backgroundColor: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            cursor: 'pointer',
                            marginBottom: '1rem'
                        }}
                    >
                        üëÅÔ∏è Previsualizar Reporte PDF
                    </button>

                    <button
                        onClick={onConfirmar}
                        style={{
                            padding: '0.7rem 2rem',
                            backgroundColor: '#28a745',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            cursor: 'pointer'
                        }}
                    >
                        ‚úÖ Confirmar Pedido
                    </button>
                </>
            ) : (
                <>
                    <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '1rem' }}>
                        <button
                            onClick={() => setMostrarPDF(false)}
                            style={{
                                padding: '0.5rem 1rem',
                                backgroundColor: '#6c757d',
                                color: 'white',
                                border: 'none',
                                borderRadius: '4px',
                                cursor: 'pointer'
                            }}
                        >
                            ‚Üê Volver
                        </button>

                        <PDFDownloadLink
                            document={<ReportePedidoPDF pedido={pedido} />}
                            fileName={`pedido_${pedido.numeroPedido}.pdf`}
                        >
                            {({ loading }) =>
                                loading ? (
                                    <button style={{ padding: '0.5rem 1rem', backgroundColor: '#ffc107' }}>
                                        Generando PDF...
                                    </button>
                                ) : (
                                    <button
                                        style={{
                                            padding: '0.5rem 1rem',
                                            backgroundColor: '#28a745',
                                            color: 'white',
                                            border: 'none',
                                            borderRadius: '4px',
                                            cursor: 'pointer'
                                        }}
                                    >
                                        üíæ Descargar PDF
                                    </button>
                                )
                            }
                        </PDFDownloadLink>
                    </div>

                    <div style={{ height: '600px', border: '1px solid #ddd', borderRadius: '8px' }}>
                        <PDFViewer width="100%" height="100%">
                            <ReportePedidoPDF pedido={pedido} />
                        </PDFViewer>
                    </div>
                </>
            )}
        </div>
    );
};

export default ConfirmacionPedido;
