import React from 'react';

const Carrito = ({ items, onRemove, total }) => {
    return (
        <div style={{ margin: '2rem 0', padding: '1rem', border: '2px solid #28a745', borderRadius: '8px' }}>
            <h3>üõí Carrito ({items.length} √≠tems)</h3>
            {items.length === 0 ? (
                <p>El carrito est√° vac√≠o</p>
            ) : (
                <>
                    <div style={{ maxHeight: '300px', overflowY: 'auto', marginBottom: '1rem' }}>
                        {items.map(item => (
                            <div key={item.id} style={{ padding: '0.7rem', borderBottom: '1px solid #eee', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                <div>
                                    <strong>{item.producto.Codigo}</strong> - {item.producto.Descripcion}<br/>
                                    Cantidad: {item.cantidad} x ${item.precioUnitario.toFixed(2)} = <strong>${item.importe.toFixed(2)}</strong>
                                </div>
                                <button
                                    onClick={() => onRemove(item.id)}
                                    style={{
                                        padding: '0.2rem 0.7rem',
                                        backgroundColor: '#dc3545',
                                        color: 'white',
                                        border: 'none',
                                        borderRadius: '4px',
                                        cursor: 'pointer'
                                    }}
                                >
                                    ‚ùå
                                </button>
                            </div>
                        ))}
                    </div>
                    <div style={{ textAlign: 'right', fontSize: '1.3rem', fontWeight: 'bold' }}>
                        üí∞ TOTAL: ${total.toFixed(2)}
                    </div>
                </>
            )}
        </div>
    );
};

export default Carrito;
