import React, { useState } from 'react';
import API from '../services/api';

const BusquedaPorCodigo = ({ listaPreciosId, onAdd }) => {
    const [codigo, setCodigo] = useState('');
    const [producto, setProducto] = useState(null);
    const [cantidad, setCantidad] = useState(1);
    const [error, setError] = useState('');

    const handleBuscar = async () => {
        if (!codigo || !listaPreciosId) {
            setError('Ingrese c√≥digo y seleccione lista de precios');
            return;
        }

        try {
            const res = await API.get(`/productos?lista=${listaPreciosId}`);
            const prod = res.data.find(p => p.Codigo === codigo);
            if (prod) {
                setProducto(prod);
                setError('');
            } else {
                setProducto(null);
                setError('Producto no encontrado');
            }
        } catch (error) {
            console.error(error);
            setError('Error al buscar producto');
        }
    };

    const handleAdd = () => {
        if (!producto) return alert("Primero busque un producto v√°lido");
        if (!cantidad || cantidad <= 0) return alert("Cantidad inv√°lida");
        onAdd(producto, parseFloat(cantidad), producto.Precio);
        setCodigo('');
        setProducto(null);
        setCantidad(1);
    };

    return (
        <div style={{ padding: '1rem', border: '1px solid #ddd', borderRadius: '8px' }}>
            <h3>üîç Buscar Producto por C√≥digo</h3>
            <div style={{ display: 'flex', gap: '0.5rem', marginBottom: '1rem' }}>
                <input
                    type="text"
                    placeholder="C√≥digo del producto"
                    value={codigo}
                    onChange={(e) => setCodigo(e.target.value.toUpperCase())}
                    style={{ flex: 1, padding: '0.5rem' }}
                />
                <button onClick={handleBuscar} style={{ padding: '0.5rem 1rem', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px' }}>
                    Buscar
                </button>
            </div>

            {error && <p style={{ color: 'red' }}>{error}</p>}

            {producto && (
                <div style={{ backgroundColor: '#f8f9fa', padding: '1rem', borderRadius: '4px', marginBottom: '1rem' }}>
                    <strong>{producto.Codigo}</strong> - {producto.Descripcion}<br/>
                    <strong>Precio:</strong> ${producto.Precio.toFixed(2)}<br/>
                    <label>Cantidad: </label>
                    <input
                        type="number"
                        value={cantidad}
                        onChange={(e) => setCantidad(e.target.value)}
                        min="1"
                        style={{ width: '60px', marginLeft: '0.5rem', padding: '0.3rem' }}
                    />
                    <button
                        onClick={handleAdd}
                        style={{ marginLeft: '1rem', padding: '0.3rem 1rem', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px' }}
                    >
                        ‚ûï Agregar
                    </button>
                </div>
            )}
        </div>
    );
};

export default BusquedaPorCodigo;
