import React, { useState } from 'react';

const ConfirmacionPedido = ({ carrito, total, cliente, onVolver, onConfirmar }) => {
    const [fechaEntrega, setFechaEntrega] = useState('');
    const [ordenCompra, setOrdenCompra] = useState('');
    const [observaciones, setObservaciones] = useState('');
    const [emailOpcional, setEmailOpcional] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!fechaEntrega) return alert("Seleccione fecha de entrega");
        // Aquí podrías enviar estos datos al backend
        console.log({
            fechaEntrega,
            ordenCompra,
            observaciones,
            emailOpcional,
            total,
            items: carrito.length
        });
        onConfirmar();
    };

    return (
        <div style={{ padding: '2rem', fontFamily: 'Arial', maxWidth: '800px', margin: 'auto' }}>
            <h2>📄 Confirmación de Pedido</h2>
            <p><strong>Cliente:</strong> {cliente?.RazonSocial}</p>
            <p><strong>Total:</strong> ${total.toFixed(2)}</p>

            <form onSubmit={handleSubmit} style={{ backgroundColor: '#f8f9fa', padding: '2rem', borderRadius: '8px' }}>
                <div style={{ marginBottom: '1rem' }}>
                    <label>📅 Fecha de Entrega: </label>
                    <input
                        type="date"
                        value={fechaEntrega}
                        onChange={(e) => setFechaEntrega(e.target.value)}
                        required
                        style={{ marginLeft: '1rem', padding: '0.3rem' }}
                    />
                </div>

                <div style={{ marginBottom: '1rem' }}>
                    <label>🔢 Orden de Compra (opcional): </label>
                    <input
                        type="text"
                        value={ordenCompra}
                        onChange={(e) => setOrdenCompra(e.target.value)}
                        style={{ marginLeft: '1rem', padding: '0.3rem', width: '200px' }}
                    />
                </div>

                <div style={{ marginBottom: '1rem' }}>
                    <label>📝 Observaciones: </label>
                    <textarea
                        value={observaciones}
                        onChange={(e) => setObservaciones(e.target.value)}
                        rows="3"
                        style={{ marginLeft: '1rem', padding: '0.3rem', width: '100%' }}
                    />
                </div>

                <div style={{ marginBottom: '1rem' }}>
                    <label>✉️ Email Opcional (para confirmación): </label>
                    <input
                        type="email"
                        value={emailOpcional}
                        onChange={(e) => setEmailOpcional(e.target.value)}
                        style={{ marginLeft: '1rem', padding: '0.3rem', width: '300px' }}
                    />
                </div>

                <div style={{ marginTop: '2rem', textAlign: 'center' }}>
                    <button
                        type="button"
                        onClick={onVolver}
                        style={{ padding: '0.7rem 2rem', backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: '4px', marginRight: '1rem' }}
                    >
                        ← Volver
                    </button>
                    <button
                        type="submit"
                        style={{ padding: '0.7rem 2rem', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '4px' }}
                    >
                        ✅ Confirmar Pedido
                    </button>
                </div>
            </form>
        </div>
    );
};

export default ConfirmacionPedido;
